mapper.select:
SELECT EX.UNITY_ID -- 파일 리스트 출력 위한 unityId ,EX.OBJECT_ID --objectId ,EX.SANCTN_ID --결재ID ,EX.STTUS_CD_ID --업무상태코드 ,EV.EVL_ID --평가ID ,EV.EVL_SJ --평가제목 ,EV.ACCNUT_YY --회계년도 ,EV.EVL_KND_CD_ID --평가종류코드ID ,(SELECT CODE_NM FROM TB_CO_CODE WHERE CODE_ID = EV.EVL_KND_CD_ID) AS EVL_KND_CD_NM --평가종류코드명 ,UP.UPRPD_CL_CD_ID --미비점분류코드 ,(SELECT CODE_NM FROM TB_CO_CODE WHERE CODE_ID = UP.UPRPD_CL_CD_ID) AS UPRPD_CL_CD_NM --미비점분류코드명 ,UP.UPRPD_TY_CD_ID --미비점유형코드 ,(SELECT CODE_NM FROM TB_CO_CODE WHERE CODE_ID = UP.UPRPD_TY_CD_ID) AS UPRPD_TY_CD_NM --미비점유형코드명 ,CR.CNRL_NM --통제명 ,CR.CNRL_ID --통제ID ,CR.CNRL_NO --통제번호 ,UP.UPRPD_IMPRVM_PLAN --미비점 개선 계획 ,UP.UPRPD_CN --미비점 발생 내용 ,EE.CHRG_USER_ID --미비점 담당 사용자ID (미비점 평가자) ,(SELECT USER_NM FROM TB_CO_USER_HIST WHERE USER_ID = EE.CHRG_USER_ID AND HIST_ENDDE = '99991231' ) AS CHRG_USER_NM --미비점 담당 사용자 이름 (미비점 평가자) ,(SELECT DEPT_NM FROM TB_CO_USER_HIST WHERE USER_ID = EE.CHRG_USER_ID AND HIST_BGNDE = EE.CHRG_USER_BGNDE ) AS CHRG_DEPT_NM --미비점 담당 사용자 부서명 (미비점 평가자) ,CASE WHEN UP.UPRPD_IMPRVM_PLAN_GOAL_DE IS NULL THEN UP.UPRPD_IMPRVM_PLAN_GOAL_DE ELSE CONVERT ( VARCHAR , CONVERT ( DATE , UP.UPRPD_IMPRVM_PLAN_GOAL_DE , 112 ) , 23 ) END AS UPRPD_IMPRVM_PLAN_GOAL_DE -- 개선계획 목표일자 ,CONVERT ( VARCHAR , EE.UPD_DT , 23 ) AS EXC_REQ_DE --미비점 개선 수행 요청일 ,EX.EXC_USER_ID FROM TB_CW_UPRPD UP ,TB_CW_UPRPD_EXC EX ,TB_CW_EVL EV ,TB_CW_EVL_RCM_CNRL CR ,TB_CW_EVL_EXC EE WHERE UP.EVL_ID = EX.EVL_ID AND UP.OBJECT_ID = EX.OBJECT_ID AND UP.EVL_ID = EV.EVL_ID AND UP.EVL_ID = CR.EVL_ID AND UP.OBJECT_ID = CR.CNRL_ID AND UP.EVL_ID = EE.EVL_ID AND UP.OBJECT_ID = EE.OBJECT_ID AND UP.CHRG_USER_ID = EE.CHRG_USER_ID AND UP.CHRG_USER_TY_CD_ID = EE.CHRG_USER_TY_CD_ID AND EX.STTUS_CD_ID IS NOT NULL AND EX.EVL_ID = #{evlId} AND EX.OBJECT_ID = #{objectId}
AND ( EX.EXC_USER_ID = #{suserId} /** 담당자로 지정된 평가 조회 */ OR EX.EXC_USER_ID IN ( SELECT A.MNDT_USER_ID FROM TB_CO_MNDT A LEFT OUTER JOIN TB_CO_MNDT_KND B ON A.MNDT_ID = B.MNDT_ID WHERE A.AGENT_USER_ID = #{suserId} AND CONVERT(VARCHAR,GETDATE(),112) + REPLACE(CONVERT(VARCHAR,GETDATE(),8),':','') BETWEEN A.MNDT_BGNDE+A.MNDT_BGNTM AND A.MNDT_ENDDE+A.MNDT_ENDTM AND A.STTUS_CD_ID = 'COM_021_005' AND B.MNDT_KND_CD_ID = 'COM_019_001' ) ) /** 위임자로 지정된 평가 조회 */
ORDER BY EX.RGS_DT DESC

mapper.update:
UPDATE TB_CW_UPRPD_EXC
SET
	EXC_CN = #{excCn}
	, EXC_SJ = #{excSj}
	, EXC_DE = REPLACE(#{excDe}, '-', '')
	, EXC_COMPT_CD_ID = #{excComptCdId}
	, STTUS_CD_ID = #{sttusCdId}
	, UNITY_ID = #{unityId}
	, UPD_DT = GETDATE()
	, UPD_USER_ID = #{suserId}
	, SANCTN_ID = #{sanctnId}
WHERE
	EVL_ID = #{evlId}
	AND OBJECT_ID = #{objectId}

