E NULL END AS VIEW_FNREP_RISKEVL_CSFCTR09 , CASE WHEN CU.CNRL_ID IS NOT NULL AND (C.FNREP_RISKEVL_CSFCTR10 != CU.FNREP_RISKEVL_CSFCTR10 OR (C.FNREP_RISKEVL_CSFCTR10 IS NOT NULL AND CU.FNREP_RISKEVL_CSFCTR10 IS NULL) OR (C.FNREP_RISKEVL_CSFCTR10 IS NULL AND CU.FNREP_RISKEVL_CSFCTR10 IS NOT NULL)) THEN ISNULL(CU.FNREP_RISKEVL_CSFCTR10 , '삭제') ELSE NULL END AS VIEW_FNREP_RISKEVL_CSFCTR10 , CASE WHEN CU.CNRL_ID IS NOT NULL AND (C.FNREP_CNRL_RISK_CNCLSN != CU.FNREP_CNRL_RISK_CNCLSN OR (C.FNREP_CNRL_RISK_CNCLSN IS NOT NULL AND CU.FNREP_CNRL_RISK_CNCLSN IS NULL) OR (C.FNREP_CNRL_RISK_CNCLSN IS NULL AND CU.FNREP_CNRL_RISK_CNCLSN IS NOT NULL)) THEN ISNULL(CU.FNREP_CNRL_RISK_CNCLSN , '삭제') ELSE NULL END AS VIEW_FNREP_CNRL_RISK_CNCLSN , CASE WHEN CU.CNRL_ID IS NOT NULL AND (C.FNREP_ACNT_RISK_CNCLSN != CU.FNREP_ACNT_RISK_CNCLSN OR (C.FNREP_ACNT_RISK_CNCLSN IS NOT NULL AND CU.FNREP_ACNT_RISK_CNCLSN IS NULL) OR (C.FNREP_ACNT_RISK_CNCLSN IS NULL AND CU.FNREP_ACNT_RISK_CNCLSN IS NOT NULL)) THEN ISNULL(CU.FNREP_ACNT_RISK_CNCLSN , '삭제') ELSE NULL END AS VIEW_FNREP_ACNT_RISK_CNCLSN , CASE WHEN CU.CNRL_ID IS NOT NULL AND (C.FNREP_PROCS_RISK_CNCLSN != CU.FNREP_PROCS_RISK_CNCLSN OR (C.FNREP_PROCS_RISK_CNCLSN IS NOT NULL AND CU.FNREP_PROCS_RISK_CNCLSN IS NULL) OR (C.FNREP_PROCS_RISK_CNCLSN IS NULL AND CU.FNREP_PROCS_RISK_CNCLSN IS NOT NULL)) THEN ISNULL(CU.FNREP_PROCS_RISK_CNCLSN , '삭제') ELSE NULL END AS VIEW_FNREP_PROCS_RISK_CNCLSN , CASE WHEN CU.CNRL_ID IS NOT NULL AND (C.FNREP_GNRLZ_EVL_CNCLSN != CU.FNREP_GNRLZ_EVL_CNCLSN OR (C.FNREP_GNRLZ_EVL_CNCLSN IS NOT NULL AND CU.FNREP_GNRLZ_EVL_CNCLSN IS NULL) OR (C.FNREP_GNRLZ_EVL_CNCLSN IS NULL AND CU.FNREP_GNRLZ_EVL_CNCLSN IS NOT NULL)) THEN ISNULL(CU.FNREP_GNRLZ_EVL_CNCLSN , '삭제') ELSE NULL END AS VIEW_FNREP_GNRLZ_EVL_CNCLSN FROM TB_CW_EVL_RCM_CNRL C LEFT OUTER JOIN TB_CW_UPRPD_CNRL_UPDT CU ON C.EVL_ID = CU.EVL_ID AND C.CNRL_ID = CU.CNRL_ID LEFT OUTER JOIN TB_CO_CODE C1 ON C.CNRL_TY01_CD_ID = C1.CODE_ID LEFT OUTER JOIN TB_CO_CODE C2 ON C.CNRL_TY02_CD_ID = C2.CODE_ID LEFT OUTER JOIN TB_CO_CODE C3 ON C.CNRL_TY03_CD_ID = C3.CODE_ID LEFT OUTER JOIN TB_CO_CODE C4 ON C.OFFICE_TY_CD_ID = C4.CODE_ID LEFT OUTER JOIN TB_CO_CODE C5 ON C.TEST_CYCLE_CD_ID = C5.CODE_ID LEFT OUTER JOIN TB_CO_CODE C6 ON CU.CNRL_TY01_CD_ID = C6.CODE_ID LEFT OUTER JOIN TB_CO_CODE C7 ON CU.CNRL_TY02_CD_ID = C7.CODE_ID LEFT OUTER JOIN TB_CO_CODE C8 ON CU.CNRL_TY03_CD_ID = C8.CODE_ID LEFT OUTER JOIN TB_CO_CODE C9 ON CU.OFFICE_TY_CD_ID = C9.CODE_ID LEFT OUTER JOIN TB_CO_CODE C10 ON CU.TEST_CYCLE_CD_ID = C10.CODE_ID LEFT OUTER JOIN TB_CO_CODE C11 ON C.CNRL_PURPS_CD_ID = C11.CODE_ID LEFT OUTER JOIN TB_CO_CODE C12 ON CU.CNRL_PURPS_CD_ID = C12.CODE_ID LEFT OUTER JOIN TB_CO_CODE C13 ON C.CNRL_CYCLE_CD_ID = C13.CODE_ID LEFT OUTER JOIN TB_CO_CODE C14 ON CU.CNRL_CYCLE_CD_ID = C14.CODE_ID LEFT OUTER JOIN TB_CO_CODE C15 ON C.CNRL_TY_CD_ID = C15.CODE_ID LEFT OUTER JOIN TB_CO_CODE C16 ON CU.CNRL_TY_CD_ID = C16.CODE_ID WHERE C.EVL_ID = #{evlId} AND C.CNRL_ID = #{objectId}

WITH V1 ( EVL_ID , OBJECT_ID , MAPNG_ID , UPPER_MAPNG_ID ) AS ( SELECT EVL_ID , OBJECT_ID , MAPNG_ID , UPPER_MAPNG_ID FROM TB_CW_EVL_RCM_MAPNG WHERE EVL_ID = #{evlId} AND OBJECT_ID = #{objectId}
UNION ALL
SELECT M.EVL_ID , M.OBJECT_ID , M.MAPNG_ID , M.UPPER_MAPNG_ID FROM TB_CW_EVL_RCM_MAPNG M , V1 ROOT WHERE M.MAPNG_ID = ROOT.UPPER_MAPNG_ID AND M.EVL_ID = ROOT.EVL_ID ) SELECT A.OBJECT_ID , A.OBJECT_KND_CD_ID , A.OBJECT_NM , A.OBJECT_NO , A.OBJECT_LEVEL , R.WCGW , R.FNLTT_DSTOR_RISK , R.RISK_EVL_RESULT , R.RISK_EVL_REASON FROM ( SELECT DISTINCT M.EVL_ID, M.MAPNG_ID, M.OBJECT_ID, M.OBJECT_NO, M.OBJECT_NM, M.OBJECT_LEVEL, M.UPPER_OBJECT_ID, M.UPPER_MAPNG_ID, M.OBJECT_KND_CD_ID FROM V1 V INNER JOIN TB_CW_EVL_RCM_MAPNG M ON V.EVL_ID = M.EVL_ID AND V.OBJECT_ID = M.OBJECT_ID WHERE M.OBJECT_KND_CD_ID != 'CTW_001_005' ) A LEFT OUTER JOIN TB_CW_EVL_RCM_RISK R ON A.EVL_ID = R.EVL_ID AND A.OBJECT_ID = R.RISK_ID ORDER BY OBJECT_ID ASC

