UPDATE TB_CW_UPRPD_EXC
SET
	UPD_DT = GETDATE()
	, UPD_USER_ID = #{suserId}
	, STTUS_CD_ID	 = #{sttusCdId}
WHERE
	EVL_ID = #{evlId}
	AND OBJECT_ID = #{objectId}

UPDATE TB_CW_UPRPD_CONFM
SET LAST_AT = 'N'
WHERE
	EVL_ID = #{evlId}
	AND OBJECT_ID = #{objectId}
	AND CONFM_SN = #{confmSn}
